ial;\n\t\t\tfont-size:26px;\n\t\t\tfont-weight:bold;\n\t\t\tline-height:100%;\n\t\t\tmargin-bottom:10px;\n\t\t\ttext-align:left;\n\t\t}\n\t\th4,.h4{\n\t\t\tcolor:#202020;\n\t\t\tdisplay:block;\n\t\t\tfont-family:Arial;\n\t\t\tfont-size:22px;\n\t\t\tfont-weight:bold;\n\t\t\tline-height:100%;\n\t\t\tmargin-bottom:10px;\n\t\t\ttext-align:left;\n\t\t}\n\t\t#templatePreheader{\n\t\t\tbackground-color:#FAFAFA;\n\t\t}\n\t\t.preheaderContent div{\n\t\t\tcolor:#505050;\n\t\t\tfont-family:Arial;\n\t\t\tfont-size:10px;\n\t\t\tline-height:100%;\n\t\t\ttext-align:left;\n\t\t}\n\t\t.preheaderContent div a:link,.preheaderContent div a:visited{\n\t\t\tcolor:#336699;\n\t\t\tfont-weight:normal;\n\t\t\ttext-decoration:underline;\n\t\t}\n\t\t.preheaderContent div img{\n\t\t\theight:auto;\n\t\t\tmax-width:600px;\n\t\t}\n\t\t#templateHeader{\n\t\t\tbackground-color:#FFFFFF;\n\t\t\tborder-bottom:0;\n\t\t}\n\t\t.headerContent{\n\t\t\tcolor:#202020;\n\t\t\tfont-family:Arial;\n\t\t\tfont-size:34px;\n\t\t\tfont-weight:bold;\n\t\t\tline-height:100%;\n\t\t\tpadding:0;\n\t\t\ttext-align:center;\n\t\t\tvertical-align:middle;\n\t\t\tbackground-color:#3B6BDB;\n\t\t}\n\t\t.headerContent a:link,.headerContent a:visited{\n\t\t\tcolor:#336699;\n\t\t\tfont-weight:normal;\n\t\t\ttext-decoration:underline;\n\t\t}\n\t\t#headerImage{\n\t\t\theight:auto;\n\t\t\tmax-width:600px !important;\n\t\t}\n\t\t#templateContainer,.bodyContent{\n\t\t\tbackground-color:#fdfdfd;\n\t\t}\n\t\t.bodyContent div{\n\t\t\tcolor:#505050;\n\t\t\tfont-family:Arial;\n\t\t\tfont-size:14px;\n\t\t\tline-height:150%;\n\t\t\ttext-align:left;\n\t\t}\n\t\t.bodyContent div a:link,.bodyContent div a:visited{\n\t\t\tcolor:#336699;\n\t\t\tfont-weight:normal;\n\t\t\ttext-decoration:underline;\n\t\t}\n\t\t.bodyContent img{\n\t\t\tdisplay:inline;\n\t\t\tmargin-bottom:10px;\n\t\t}\n\t\t#templateFooter{\n\t\t\tbackground-color:#FDFDFD;\n\t\t\tborder-top:0;\n\t\t}\n\t\t.footerContent div{\n\t\t\tcolor:#707070;\n\t\t\tfont-family:Arial;\n\t\t\tfont-size:12px;\n\t\t\tline-height:125%;\n\t\t\ttext-align:left;\n\t\t}\n\t\t.footerContent div a:link,.footerContent div a:visited{\n\t\t\tcolor:#336699;\n\t\t\tfont-weight:normal;\n\t\t\ttext-decoration:underline;\n\t\t}\n\t\t.footerContent img{\n\t\t\tdisplay:inline;\n\t\t}\n\t\t#social{\n\t\t\tbackground-color:#FAFAFA;\n\t\t\tborder:1px solid #F5F5F5;\n\t\t}\n\t\t#social div{\n\t\t\ttext-align:center;\n\t\t}\n\t\t#utility{\n\t\t\tbackground-color:#FDFDFD;\n\t\t\tborder-top:1px solid #F5F5F5;\n\t\t}\n\t\t#utility div{\n\t\t\ttext-align:center;\n\t\t}\n\t\t#monkeyRewards img{\n\t\t\tmax-width:160px;\n\t\t}\n</style></head>\n    <body leftmargin=\"0\" marginwidth=\"0\" topmargin=\"0\" marginheight=\"0\" offset=\"0\" style=\"-webkit-text-size-adjust: none;margin: 0;padding: 0;background-color: #FAFAFA;width: 100% !important;\">\n        <center>\n            <table border=\"0\" cellpadding=\"0\" cellspacing=\"0\" height=\"100%\" width=\"100%\" id=\"backgroundTable\" style=\"margin: 0;padding: 0;height: 100% !important;width: 100% !important;\">\n                <tr>\n                    <td align=\"center\" valign=\"top\">\n                        <!-- // Begin Template Preheader \\\\ -->\n                        <table border=\"0\" cellpadding=\"10\" cellspacing=\"0\" width=\"600\" id=\"templatePreheader\" style=\"background-color: #FAFAFA;\">\n                            <tr>\n                                <td valign=\"top\" class=\"preheaderContent\">\n                                \n                                    <!-- // Begin Module: Standard Preheader \\\\ -->\n                                    <table border=\"0\" cellpadding=\"10\" cellspacing=\"0\" width=\"100%\">\n                                        <tr>\n                                            <td valign=\"top\">\n                                                <div style=\"color: #505050;font-family: Arial;font-size: 10px;line-height: 100%;text-align: left;\">This is introducing you to MailChimp, a new vehicle setup to deliver NYSOMA updates.</div>\n                                            </td>\n                                            <td valign=\"top\" width=\"180\">\n                                                <div style=\"color: #505050;font-family: Arial;font-size: 10px;line-height: 100%;text-align: left;\">\n                                                    <!-- *|IFNOT:ARCHIVE_PAGE|* -->Is this email not displaying correctly?<br><a href=\"*|ARCHIVE|*\" target=\"_blank\" style=\"color: #336699;font-weight: normal;text-decoration: underline;\">View it in your browser</a>.<!-- *|END:IF|* -->\n                                                </div>\n                                            </td>\n                                        </tr>\n                                    </table>\n                                    <!-- // End Module: Standard Preheader \\\\ -->\n                                \n                                </td>\n                            </tr>\n                        </table>\n                        <!-- // End Template Preheader \\\\ -->\n                        <table border=\"0\" cellpadding=\"0\" cellspacing=\"0\" width=\"600\" id=\"templateContainer\" style=\"border: 1px solid #DDDDDD;background-color: #fdfdfd;\">\n                            <tr>\n                                <td align=\"center\" valign=\"top\">\n                                    <!-- // Begin Template Header \\\\ -->\n                                    <table border=\"0\" cellpadding=\"0\" cellspacing=\"0\" width=\"600\" id=\"templateHeader\" style=\"background-color: #FFFFFF;border-bottom: 0;\">\n                                        <tr>\n                                            <td class=\"headerContent\" style=\"color: #202020;font-family: Arial;font-size: 34px;font-weight: bold;line-height: 100%;padding: 0;text-align: center;vertical-align: middle;background-color: #3B6BDB;\">\n                                            \n                                                <!-- // Begin Module: Standard Header Image \\\\ -->\n                                                <img src=\"http://www.nysoma.org/wp-content/themes/nysoma/images/logo.png\" alt=\"\" border=\"0\" style=\"margin: 0;padding: 0;max-width: 600px;border: none;font-size: 14px;font-weight: bold;height: auto;line-height: 100%;outline: none;text-decoration: none;text-transform: capitalize;\" id=\"headerImage campaign-icon\">\n                                                <!-- // End Module: Standard Header Image \\\\ -->\n                                            \n                                            </td>\n                                        </tr>\n                                    </table>\n                                    <!-- // End Template Header \\\\ -->\n                                </td>\n                            </tr>\n                            <tr>\n                                <td align=\"center\" valign=\"top\">\n                                    <!-- // Begin Template Body \\\\ -->\n                                    <table border=\"0\" cellpadding=\"10\" cellspacing=\"0\" width=\"600\" id=\"templateBody\">\n                                        <tr>\n                                            <td valign=\"top\" class=\"bodyContent\" style=\"background-color: #fdfdfd;\">\n                                \n                                                <!-- // Begin Module: Standard Content \\\\ -->\n                                                <table border=\"0\" cellpadding=\"10\" cellspacing=\"0\" width=\"100%\">\n                                                    <tr>\n                                                        <td valign=\"top\">\n                                                            <div style=\"color: #505050;font-family: Arial;font-size: 14px;line-height: 150%;text-align: left;\">Hi *|FNAME|*,<br>\n<br>\nYou are receiving this message because you are a part of the NYSOMA Board. This is being sent through MailChimp. We have a free account setup through them now which allows us to have up to 2,000 subscribers and send up to 12,000 individual emails per month. If we need to go over that, it is a small monthly charge.&nbsp;<br>\n<br>\nThe intent of this is to be able to quickly and easily target many people with membership news, event information, and our newsletters. For the most part, we will be linking back to the website via these messages.&nbsp;<br>\n<br>\nAll of you have been imported as subscribers of type NYSOMA Board. Other contact types have been setup for when we import subscribers, we can target certain groups, such as membership, teams, coaches, or just regular subscribers. A form has been setup on the website. You will begin to see a yellow bar across the top of each page enticing people to signup. This, hopefully, will grow membership as we can send information about the organization.<br>\n<br>\nHope you enjoy your first message!</div>\n                                                        </td>\n                                                    </tr>\n                                                </table>\n                                                <!-- // End Module: Standard Content \\\\ -->\n                                                \n                                            </td>\n                                        </tr>\n                                    </table>\n                                    <!-- // End Template Body \\\\ -->\n                                </td>\n                            </tr>\n                            <tr>\n                                <td align=\"center\" valign=\"top\">\n                                    <!-- // Begin Template Footer \\\\ -->\n                                    <table border=\"0\" cellpadding=\"10\" cellspacing=\"0\" width=\"600\" id=\"templateFooter\" style=\"background-color: #FDFDFD;border-top: 0;\">\n                                        <tr>\n                                            <td valign=\"top\" class=\"footerContent\">\n                                            \n                                                <!-- // Begin Module: Standard Footer \\\\ -->\n                                                <table border=\"0\" cellpadding=\"10\" cellspacing=\"0\" width=\"100%\">\n                                                    <tr>\n                                                        <td colspan=\"2\" valign=\"middle\" id=\"social\" style=\"background-color: #FAFAFA;border: 1px solid #F5F5F5;\">\n                                                            <div style=\"color: #707070;font-family: Arial;font-size: 12px;line-height: 125%;text-align: center;\">&nbsp;<a href=\"http://www.facebook.com/pages/New-York-Odyssey-of-the-Mind/188827104538952\" style=\"color: #336699;font-weight: normal;text-decoration: underline;\">friend on Facebook</a> | <a href=\"*|FORWARD|*\" style=\"color: #336699;font-weight: normal;text-decoration: underline;\">forward to a friend</a>&nbsp;</div>\n                                                        </td>\n                                                    </tr>\n                                                    <tr>\n                                                        <td valign=\"top\" width=\"370\">\n                                                            <br>\n                                                            <div style=\"color: #707070;font-family: Arial;font-size: 12px;line-height: 125%;text-align: left;\">\n                                                                *|IF:LIST|*\n                                                                <em>Copyright &copy; *|CURRENT_YEAR|* *|LIST:COMPANY|*, All rights reserved.</em>\n                                                                <br>\n                                                                <!-- *|IFNOT:ARCHIVE_PAGE|* -->\n                                                                <br>\n                                                                <strong>Our mailing address is:</strong>\n                                                                <br>\n                                                                *|HTML:LIST_ADDRESS_HTML|*\n                                                                <br>\n                                                                <!-- *|END:IF|* -->\n                                                                *|ELSE:|*\n                                                                <!-- *|IFNOT:ARCHIVE_PAGE|* -->\n                                                                <em>Copyright &copy; *|CURRENT_YEAR|* *|USER:COMPANY|*, All rights reserved.</em>\n                                                                <br>\n                                                                <strong>Our mailing address is:</strong>\n                                                                <br>\n                                                                *|USER:ADDRESS_HTML|*\n                                                                <!-- *|END:IF|* -->\n                                                                *|END:IF|*\n                                                            </div>\n                                                            <br>\n                                                        </td>\n                                                        <td valign=\"top\" width=\"170\" id=\"monkeyRewards\">\n                                                            <br>\n                                                            <div style=\"color: #707070;font-family: Arial;font-size: 12px;line-height: 125%;text-align: left;\">\n                                                                *|IF:REWARDS|* *|HTML:REWARDS|* *|END:IF|*\n                                                            </div>\n                                                            <br>\n                                                        </td>\n                                                    </tr>\n                                                    <tr>\n                                                        <td colspan=\"2\" valign=\"middle\" id=\"utility\" style=\"background-color: #FDFDFD;border-top: 1px solid #F5F5F5;\">\n                                                            <div style=\"color: #707070;font-family: Arial;font-size: 12px;line-height: 125%;text-align: center;\">\n                                                                &nbsp;<a href=\"*|UNSUB|*\" style=\"color: #336699;font-weight: normal;text-decoration: underline;\">unsubscribe from this list</a> | <a href=\"*|UPDATE_PROFILE|*\" style=\"color: #336699;font-weight: normal;text-decoration: underline;\">update subscription preferences</a><!-- *|IFNOT:ARCHIVE_PAGE|* --> | <a href=\"*|ARCHIVE|*\" style=\"color: #336699;font-weight: normal;text-decoration: underline;\">view email in browser</a><!-- *|END:IF|* -->&nbsp;\n                                                            </div>\n                                                        </td>\n                                                    </tr>\n                                                </table>\n                                                <!-- // End Module: Standard Footer \\\\ -->\n                                            \n                                            </td>\n                                        </tr>\n                                    </table>\n                                    <!-- // End Template Footer \\\\ -->\n                                </td>\n                            </tr>\n                        </table>\n                        <br>\n                    </td>\n                </tr>\n            </table>\n        </center>\n    </body>\n</html>\n\n"}]}'
